import React, { useState, useEffect } from 'react';

function Details(props) {
  console.log(props);
  const [citiesOld, setCitiesOld] = useState(null);

  // eslint-disable-next-line no-unused-vars
  // eslint-disable-next-line react/prop-types
  const { countryName, data, src } = props;

  useEffect(() => {
    if (data !== citiesOld) {
      setCitiesOld(data);
    }
  });

  return (
    <div className="details">
      <div className="details-head">
        <img src={src} alt="country" className="country" />
        <div className="details-data">
          <h4 className="details-title">{countryName}</h4>
          <h6 className="cities">5 Cities</h6>
        </div>
        <div>
          {citiesOld
            ? citiesOld.map((c) => (
              <div key={c.name}>
                <h4 className="city-name">{c.name}</h4>
                <h5 className="city-temp">{c.temp}</h5>
                <h5 className="city-main">{c.weather[0].main}</h5>
                <div className="city-max-min">
                  <h5 className="city-temp-min">{c.temp_min}</h5>
                  /
                  <h5 className="city-temp-max">{c.temp_max}</h5>
                </div>
              </div>
            ))
            : null}
        </div>
      </div>
    </div>
  );
}

export default Details;
