import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import Card from './Card';

function Displayer() {
  const { locationsReducer } = useSelector((state) => state);
  const { locations } = locationsReducer;
  // eslint-disable-next-line no-unused-vars
  const [locationsDisplay, setLocationsDisplay] = useState(null);

  // Similar to componentDidMount and componentDidUpdate:
  useEffect(() => {
    console.log(locations);
    if (locations) {
      const countries = Object.entries(locations).map((country) => country);
      if (locationsDisplay !== countries) {
        console.log(countries);
       // setLocationsDisplay(countries);
      }
    }
  });

  if (locationsDisplay) {
    return (
      <div className="locations-displayer">
        <h6 className="stats">STATS BY COUNTRY</h6>
        {locationsDisplay.length > 0
          // eslint-disable-next-line react/no-array-index-key
          ? locationsDisplay.map((c, index) => <Card key={index} data={c} />)
          : null}
      </div>
    );
  }
  return <h3>Empty collection</h3>;
}

export default Displayer;
