/* eslint-disable camelcase */
import React from 'react';
import PropTypes from 'prop-types';

function Card(props) {
  const { img, data, country } = props;
  console.log(data);
  console.log(country);
  const cityName = Object.keys(data)[0];
  const location = data[cityName];
  const { main, weather } = location;
  const { temp, temp_max, temp_min } = main;
  const mainWeather = weather[0].main;
  return (
    <div className="card">
      <img src={img} alt="imagecountry" className="img-country" />
      <h4 className="name-country">{country}</h4>
      <h5 className="temperature-country">{temp}</h5>
      <h5 className="main-country">{mainWeather}</h5>
      <div className="max-min">
        <h6 className="temperature-min-country">{temp_min}</h6>
        <h6 className="temperature-max-country">{temp_max}</h6>
      </div>
    </div>
  );
}

export default Card;
Card.propTypes = { img: PropTypes.string.isRequired };
Card.propTypes = { data: PropTypes.string.isRequired };
Card.propTypes = { country: PropTypes.string.isRequired };
