/*  Extract the countries and the capital to get :
- country name
- country temp, max-temp, min-temp, main
-
*/
const RECEIVE_LOCATIONS = 'locationStore/locations/RECEIVE_LOCATIONS';
const FETCH_CITIES = 'locationStore/locations/FETCH_CITIES';

export const fetchCities = (cities) => ({
  type: FETCH_CITIES,
  cities,
});

const initialState = {
  countries: ['Finland', 'Sweden', 'Norway', 'Denmark'],
  locations: null,
  cities: [],
};

const reducer = (state = initialState, action) => {
  console.log(action);
  switch (action.type) {
    case RECEIVE_LOCATIONS: {
      const { locations } = action;
      const { europe } = locations;
      const { countries, cities } = state;
      return { europe, countries, cities };
    }
    case FETCH_CITIES: {
      const { cities } = action;
      const { countries, europe } = state;
      return { europe, countries, cities };
    }
    default:
      return state;
  }
};

export default reducer;
