import axios from 'axios';

const url = 'https://api.openweathermap.org/data/2.5/find?';
const appId = '7d21fcc19d2a5ea9e47384b584b78a2e';

const cities = [
  'oulu',
  'helsinki',
  'tornio',
  'kemili',
  'lulea',
  'uppsala',
  'goteborg',
  'stockholm',
  'oslo',
  'tromso',
  'vardo',
  'bergen',
  'copenhagen',
  'helsingor',
  'skagen',
  'alborg',
];

const request = (city) => axios.get(`${url}q=${city}&appid=${appId}`);

const process = (result) => {
  let location = null;
  console.log(result);
  if (location) {
    if (result.status === 200) {
      const { data } = result;
      const { list } = data;
      // eslint-disable-next-line prefer-destructuring
      location = list[0];
    }
  }
  return location;
};

const getLocations = async () => {
  const axiosOulu = request(cities[0]);
  const axiosHelsinki = request(cities[1]);
  const axiosTornio = request(cities[2]);
  const axiosKemili = request(cities[3]);
  const axiosLulea = request(cities[4]);
  const axiosUppsala = request(cities[5]);
  const axiosGoteborg = request(cities[6]);
  const axiosStockholm = request(cities[7]);
  const axioOsslo = request(cities[8]);
  const axiosTromso = request(cities[9]);
  const axiosVardo = request(cities[10]);
  const axiosBergen = request(cities[11]);
  const axiosCopenhagen = request(cities[12]);
  const axiosHelsingor = request(cities[13]);
  const axiosSkagen = request(cities[14]);
  const axiosAlborg = request(cities[15]);

  await axios
    .all([
      axiosOulu,
      axiosHelsinki,
      axiosTornio,
      axiosKemili,
      axiosLulea,
      axiosUppsala,
      axiosGoteborg,
      axiosStockholm,
      axioOsslo,
      axiosTromso,
      axiosVardo,
      axiosBergen,
      axiosCopenhagen,
      axiosHelsingor,
      axiosSkagen,
      axiosAlborg,
    ])
    .then(
      axios.spread(
        (
          res0,
          res1,
          res2,
          res3,
          res4,
          res5,
          res6,
          res7,
          res8,
          res9,
          res10,
          res11,
          res12,
          res13,
          res14,
          res15,
        ) => {
          const locations = {
            europe: {
              finland: {
                oulu: res0,
                helsinki: res1,
                tornio: res2,
                kemili: res3,
              },
              sweden: {
                lulea: res4,
                uppsala: res5,
                goteborg: res6,
                stockholm: res7,
              },
              norway: {
                oslo: res8,
                tromso: res9,
                vardo: res10,
                bergen: res11,
              },
              denmark: {
                copenhagen: res12,
                helsingor: res13,
                skagen: res14,
                alborg: res15,
              },
            },
          };
          return locations;
        },
      ),
    );
};
export default { getLocations };
