/* eslint-disable no-unused-vars */
import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector, store } from 'react-redux';
import Card from './Card';
import Finland from '../imgs/finland.png';
import Norway from '../imgs/norway.png';
import Sweden from '../imgs/sweden.png';
import Denmark from '../imgs/denmark.png';
import { filterCountries } from '../redux/filter/filter';

function Displayer() {
  const dispatch = useDispatch();
  const { locationsReducer } = useSelector((state) => state);
  const { filtersReducer } = useSelector((state) => state);
  const { europe, countries } = locationsReducer;
  const { filterCountries } = filtersReducer;
  const [countriesOld, setCountriesOld] = useState(null);
  const [displayFinland, setDisplayFinland] = useState(null);
  const [displayNorway, setDisplayNorway] = useState(null);
  const [displayDenmark, setDisplayDenmark] = useState(null);
  const [displaySweden, setDisplaySweden] = useState(null);

  const clearStates = () => {
    setDisplayFinland(null);
    setDisplayNorway(null);
    setDisplaySweden(null);
    setDisplayDenmark(null);
  };

  const setStates = (data) => {
    data.forEach((c) => {
      if (c === 'Finland') {
        setDisplayFinland(true);
      }
      if (c === 'Norway') {
        setDisplayNorway(true);
      }
      if (c === 'Sweden') {
        setDisplaySweden(true);
      }
      if (c === 'Denmark') {
        setDisplayDenmark(true);
      }
    });
  };

  const filter = (e) => {
    const { value } = e.target;
    const data = { value };
    dispatch(filterCountries(data));
  };

  useEffect(() => {
    if (!filterCountries) {
      if (countries !== countriesOld) {
        clearStates();
        setStates(countries);
        setCountriesOld(countries);
      }
    } else if (countries !== countriesOld) {
      clearStates();
      setStates(countries);
      setCountriesOld(countries);
    }
  });

  return (
    <div className="locations-displayer">
      <div className="stats">
        <h6 className="stats">STATS BY COUNTRY</h6>
        <select
          className="filter-category"
          placeholder="Category"
          onChange={filter}
          id="input-category"
          name="input-category"
        >
          <option value="-"> - </option>
          <option value="finland">Finland</option>
          <option value="sweden">Sweden</option>
          <option value="norway">Norway</option>
          <option value="denmark">Denmark</option>
        </select>
      </div>

      {europe ? (
        <div className="displayer-countries">
          {countriesOld ? (
            <Card data={europe.Finland} img={Finland} country="Finland" />
          ) : null}
          {displayNorway ? (
            <Card data={europe.Norway} img={Norway} country="Norway" />
          ) : null}
          {displaySweden ? (
            <Card data={europe.Sweden} img={Sweden} country="Sweden" />
          ) : null}

          {displayDenmark ? (
            <Card data={europe.Denmark} img={Denmark} country="Denmark" />
          ) : null}
        </div>
      ) : (
        <h5 className="empty">Empty Collection</h5>
      )}
    </div>
  );
}

export default Displayer;
